import {
  INCREMENT_REQUEST,
  DECREMENT_REQUEST,
  INCREMENT_SUCCESS,
} from "./actionTypes";

const delay = (ms) => new Promise((res) => setTimeout(res, ms));

// const delayIncrement = (step, ) => {
//   return
// };

export const incrementActionBegin = (step) => {
  return function (dispatch) {
    dispatch({
      type: INCREMENT_REQUEST,
    });
    // delay(2000);
  };
};

export const incrementActionSuccess = (step) => {
  return {
    type: INCREMENT_SUCCESS,
    payload: {
      step: step,
    },
  };
};

export const decrementAction = (step) => {
  return {
    type: DECREMENT_REQUEST,
    payload: {
      step: step,
    },
  };
};
